definition.script:
throttle(['Lock']) {
    node {
        try {
            def remote = [:]

            withCredentials([sshUserPrivateKey(credentialsId: 'ssh', keyFileVariable: 'file', passphraseVariable: 'pass', usernameVariable: 'user')]) {
                remote.identityFile = file
                remote.name = 'backend'
                remote.host = "${VMIP}"
                remote.user = 'azureuser'
                remote.allowAnyHosts = true    

                stage ('Cleanup') {
                    writeFile file: 'cleanup.sh', text:
                    "rm -rf teachbackimage"
                    sshScript remote: remote, script: "cleanup.sh"
                }

                stage ('Scm checkout') {
                    git branch: 'backImage', url: 'https://github.com/Nazar802/demo3.git'
                    sshCommand remote: remote, command: "git clone --branch backImage https://github.com/Nazar802/demo3.git teachbackimage"
                }

                stage ('Mvn Package') {
                    writeFile file: 'package.sh', text: 
                    "cd ~/teachbackimage\nmvn package"
                    sshScript remote: remote, script: "package.sh"
                }

                stage ('Docker Build') {
                    writeFile file: 'build.sh', text: 
                    "cd ~/teachbackimage\ndocker build . -t ${ACR_ADDR}/backend:latest"
                    sshScript remote: remote, script: "build.sh"
                }

                stage ('Docker Push') {
                    writeFile file: 'push.sh', text:
                    "docker push ${ACR_ADDR}/backend:latest"
                    sshScript remote: remote, script: "push.sh"
                }

            }
            withCredentials([string(credentialsId: 'chatID', variable: 'notif')]) {
                sh  ("""
                curl -s -X POST https://api.telegram.org/bot${TELEGRAM_TOKEN}/sendMessage -d chat_id=${CHAT_ID} -d parse_mode=markdown -d text='Oh Yeah! Kubernetes job succeeded'
                """)
            }
        }
        catch (e) {
            withCredentials([string(credentialsId: 'chatID', variable: 'notif')]) {
                sh  ("""
                curl -s -X POST https://api.telegram.org/bot${TELEGRAM_TOKEN}/sendMessage -d chat_id=${CHAT_ID} -d parse_mode=markdown -d text="Fuck You! Kubernetes job failed"
                """)
            }
            throw e
        }
    }
}
