definition.script:
node {
    def remote = [:]

    withCredentials([sshUserPrivateKey(credentialsId: 'ssh', keyFileVariable: 'file', passphraseVariable: 'pass', usernameVariable: 'user')]) {
        remote.identityFile = file
        remote.name = 'backend'
        remote.host = "${VMIP}"
        remote.user = 'azureuser'
        remote.allowAnyHosts = true

        stage ('Install') {
            writeFile file: 'start.sh', text:
            "kubectl create namespace monitoring || true\nhelm upgrade --install prometheus --namespace monitoring stable/prometheus-operator --values values.yml || true"
            sshScript remote: remote, script: "start.sh"
        }

        stage ('Start monitoring') {
            writeFile file: 'start.sh', text:
            "pkill kubectl\nkubectl port-forward --address 0.0.0.0 deploy/prometheus-grafana --namespace=monitoring 3000"
            sshScript remote: remote, script: "start.sh"
        }
    }
}
